class DecimalEncoder2(json.JSONEncoder):
  def default(self, myvar):
    if isinstance(myvar, Decimal):
      return float(myvar)
    return super(DecimalEncoder2, self).default(myvar)
class DecimalEncoder(json.JSONEncoder):
  def default(self, myvar):
    if isinstance(myvar, Decimal):
      return ('Decimal:' + str(myvar))
    return super(DecimalEncoder2, self).default(myvar)

