        if 'total' in liq:
            spn['total']=liq['total']
        if 'ltotal' in liq:
            spn['ltotal']=liq['ltotal']
        if 'rtotal' in liq:
            spn['rtotal']=liq['rtotal']
        if 'rating' in liq:
            spn['rating']=liq['rating']
        if 'future' in liq:
            spn['future']=liq['future']
        if 'nfreeze' in liq:
            spn['nfreeze']=liq['nfreeze']
        if 'supply' in liq:
            spn['supply']=liq['supply']
        return spn
