def waitlock(limit=1000):
    global LockTHIS
    lktik=0
    while 'AP' in LockTHIS or 'EW' in LockTHIS or 'BM' in LockTHIS or 'UD' in LockTHIS:
        lktik+=1
        time.sleep(.01)
        if lktik==limit:
            break
    if 'AP' not in LockTHIS and 'EW' not in LockTHIS and 'BM' not in LockTHIS and 'UD' not in LockTHIS:
        return True
    else:
        return False
