def addmonths(m,x):
    i=0
    while i<m:
        try:
            x = (x.replace(day=1) + datetime.timedelta(days=31)).replace(day=x.day)
        except ValueError:  # January 31 will return last day of February.
            x = (x + datetime.timedelta(days=31)).replace(day=1) - datetime.timedelta(days=1)
        i+=1
    return x
