    def CalculateBarter(self, myitem2, quantity=''):
        #Dif=(Decimal(self.order['Market Data']['rate'])/Decimal(self.rate)).quantize(Decimal('.00000001'), rounding=ROUND_HALF_UP)
        myitem=json_deep_copy(myitem2,1)
        rt=self.rate
