    def BufferCheck(self, spendme, contract=0):
        future=0
        liquid=0
        for i in spendme:
            if contract==0:
                future+=i['liquidity']['nfreeze']
            else:
                future+=i['liquidity']['future']
            liquid+=i['liquidity']['ltotal']
        return future, liquid
